mapper.select:
select
ap.id,a.mobile,a.nick_name as nickName,p.name,p.content,p.price,p.agent_percent as
agentPercent,ap.status,ap.create_time as createTime
from
beauty_agent_project ap
left join beauty_agent a on a.id = ap.agent_id
left join beauty_project p on p.id = ap.project_id

    and ap.agent_id = #{agentId}
    and ap.status = #{status}



        order by ${sort} ${order}


        order by ap.id desc



    limit #{offset}, #{limit}

select ap.id,a.mobile,a.nick_name as nickName,p.name,p.content,p.price,p.agent_percent as agentPercent,ap.status,ap.create_time as createTime from beauty_agent_project ap left join beauty_agent a on a.id = ap.agent_id left join beauty_project p on p.id = ap.project_id
and a.mobile LIKE '%${ mobile}%' and a.nick_name LIKE '%${ nickName}%' and p.name LIKE '%${ name}%'
order by ${sort} ${order}
order by ap.id desc
limit #{offset}, #{limit}

mapper.update:
update beauty_agent_project
status = #{status}, create_time = #{createTime}, check_time = #{checkTime}, pay_time = #{payTime},
